-- Migration: Core Content Structure\n-- Author: Developer\n-- Date: 2025\n\n-- Books Table\nCREATE TABLE books (\n    id SERIAL PRIMARY KEY,\n    title TEXT NOT NULL,\n    release_date DATE,\n    created_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP\n);\n\n-- Volumes Table\nCREATE TABLE volumes (\n    id SERIAL PRIMARY KEY,\n    book_id INT REFERENCES books(id),\n    title TEXT NOT NULL,\n    release_date DATE,\n    created_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP\n);\n\n-- Sagas Table\nCREATE TABLE sagas (\n    id SERIAL PRIMARY KEY,\n    volume_id INT REFERENCES volumes(id),\n    title TEXT NOT NULL,\n    release_date DATE,\n    created_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP\n);\n\n-- Arcs Table\nCREATE TABLE arcs (\n    id SERIAL PRIMARY KEY,\n    saga_id INT REFERENCES sagas(id),\n    title TEXT NOT NULL,\n    release_date DATE,\n    created_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP\n);\n\n-- Issues Table\nCREATE TABLE issues (\n    id SERIAL PRIMARY KEY,\n    arc_id INT REFERENCES arcs(id),\n    title TEXT NOT NULL,\n    release_date DATE,\n    created_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP\n);\n
